import unittest


class Solution(object):
    def function(self, string):

        for i in range(3, len(string)):
            left = i - 3
            right = i
            substring = string[left:right+1]
            if sorted(list(substring)) == sorted(list(set(substring))):
                ans = right + 1
                print("answer:", ans)
                return ans





class TestCase(unittest.TestCase):

    def test_dev(self):
        assert Solution().function("mjqjpqmgbljsphdztnvjfqwrcgsmlb") == 7

    def test_prod(self):
        assert Solution().function("") == 1
